0!5e0!3m2!1svi!2s!4v1435157813823', '');
INSERT INTO `tour_content` (`tour_content_code`, `tour_region_code`, `title`, `time`, `total_cost`, `content`, `content_cost`, `description`, `image`, `update_at`, `create_at`, `tour_country_code`, `possition`, `image_relate`) VALUES
(28, 12, 'Di sản miền trung 2015 Đà nẵng - Hội an - Huế - Động thiên đường', '4 Ngày 3 Đêm', 'Liên Hệ', '<table class="MsoNormalTable" border="0" cellspacing="0" cellpadding="0" width="631" style="width:473.25pt;mso-cellspacing:0in;mso-yfti-tbllook:1184;mso-padding-alt:\r\n 0in 0in 0in 0in">\r\n <tbody><tr>\r\n  <td width="103" style="width:77.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">NGÀY 01</span></b><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n  <td width="631" style="width:473.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" align="left" style="line-height: normal;"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">ĐÀ NẴNG – BÀ NÀ - NGŨ HÀNH SƠN – PHỐ CỔ HỘI AN&nbsp;&nbsp;&nbsp;\r\n  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(Ăn:\r\n  trưa tối)</span></b><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n </tr>\r\n <tr>\r\n  <td width="103" style="width:77.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  line-height:normal"><font size="2"><span style="font-family: Arial, sans-serif; color: black;">Sáng</span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n  <td width="631" style="width:473.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  text-align:justify;line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">Qúy khách có mặt tại sân bay, tự túc làm thủ tục chuyến bay đến\r\n  Đà Nẵng (có mặt tại sân bay Đà Nẵng trước 08h00).</span></b><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  text-align:justify;line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">08h00:&nbsp;Đến Đà Nẵng, &nbsp;x</span></b><span style="font-family: Arial, sans-serif; color: black;">e và hướng dẫn viên đón khách khởi hành đi khu\r\n  du lịch<b>&nbsp;Bà Nà – Núi Chúa</b>, nơi mà quý khách khám phá những khoảnh\r\n  khắc giao mùa bất ngờ Xuân – Hạ - Thu – Đông trong 1 ngày. Ngồi cáp treo dài\r\n  nhất thế giới (gần 6.000m<b>) (vé cáp treo tự túc),</b>&nbsp;tham quan&nbsp;<b>Đồi\r\n  Vọng Nguyệt, chùa Linh Ứng, Thích Ca Phật Đài, khu chuồng ngựa cũ của Pháp,\r\n  vườn tịnh tâm và đỉnh nhà rông.</b>&nbsp;Tiếp tục ngồi cáp treo đến đỉnh\r\n  Nghinh Phong, biệt thự Lệ Nim, Lầu Vọng Nguyệt, Cầu Treo Bà Nà và chinh phục\r\n  đỉnh núi Chúa ở độ cao 1.487m so với mực nước biển để thưởng thức quang cảnh\r\n  núi rừng Bà Nà và toàn cảnh Đà Nẵng và Quảng Nam trên cao, tham gia các trò\r\n  chơi phiêu lưu mới lạ, hấp dẫn, hiện đại như vòng quay tình yêu, phi công\r\n  Skiver, đường đua lửa, xe điện đụng, ngôi nhà ma.</span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n </tr>\r\n <tr>\r\n  <td width="103" style="width:77.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  line-height:normal"><font size="2"><span style="font-family: Arial, sans-serif; color: black;">Trưa</span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n  <td width="631" style="width:473.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  text-align:justify;line-height:normal"><font size="2"><span style="font-family: Arial, sans-serif; color: black;">Ăn trưa tại nhà hàng.</span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n </tr>\r\n <tr>\r\n  <td width="103" style="width:77.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  line-height:normal"><font size="2"><span style="font-family: Arial, sans-serif; color: black;">Chiều</span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n  <td width="631" style="width:473.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  text-align:justify;line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">13h30:&nbsp;</span></b><span style="font-family: Arial, sans-serif; color: black;">Đoàn\r\n  khởi hành tham quan khu di tích&nbsp;<b>Làng Nghề Điêu Khắc Đá&nbsp;Non\r\n  Nước,&nbsp;</b>xem các nghệ nhân xây dựng nhiều tác phẩm trên đã chuyên\r\n  nghiệp.</span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  text-align:justify;line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">15h00</span></b><span style="font-family: Arial, sans-serif; color: black;">: Chia tay làng điêu<b>khắc\r\n  đá Non Nước đoàn&nbsp;</b>khởi hành đến&nbsp;<b>Hội An,&nbsp;</b>quý khách\r\n  bách bộ tham quan mua sắm tại&nbsp;<b>Phố Cổ Hội An</b>&nbsp;– Nơi được\r\n  UNESCO công nhận là Di sản văn hóa thế giới, với các điểm tham quan:&nbsp;<b><i>Chùa\r\n  Cầu Nhật Bản,&nbsp;Bảo tàng văn hóa Sa Huỳnh,Nhà Cổ hàng trăm năm\r\n  tuổi,&nbsp;Hội Quán Phước Kiến &amp; Xưởng thủ công mỹ nghệ.</i></b></span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  text-align:justify;line-height:normal"><font size="2"><span style="font-family: Arial, sans-serif; color: black;">Tự do mua sắm các mặt hàng lưu niệm phong phú nơi đây..</span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n </tr>\r\n <tr>\r\n  <td width="103" style="width:77.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  line-height:normal"><font size="2"><span style="font-family: Arial, sans-serif; color: black;">Tối</span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n  <td width="631" style="width:473.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  text-align:justify;line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">19h00:</span></b><span style="font-family: Arial, sans-serif; color: black;">&nbsp; Qúy khách ăn\r\n  tối tại nhà hàng, thưởng thức&nbsp;<b>đặc sản Hội An</b>&nbsp;(<i>Cao Lầu -\r\n  Bánh Bao - Bánh Vạc - Hoành Thánh</i>).</span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  text-align:justify;line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">20h00</span></b><span style="font-family: Arial, sans-serif; color: black;">: Xe đưa quý khách về\r\n  lại Đà Nẵng dọc đường biển để ngắm thành phố Đà Nẵng tuyệt đẹp về đêm với\r\n  hàng loạt khu nghỉ dưỡng và Resort cao cấp. Ngủ đêm tại khách sạn tại Đà\r\n  Nẵng.</span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n </tr>\r\n <tr>\r\n  <td width="103" style="width:77.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">NGÀY 02</span></b><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n  <td width="631" style="width:473.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  text-align:justify;line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">ĐÀ NẴNG – ĐÈO HẢI VÂN - CỐ ĐÔ\r\n  HUẾ&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(Ăn:\r\n  sáng, trưa, tối)</span></b><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n </tr>\r\n <tr>\r\n  <td width="103" style="width:77.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  line-height:normal"><font size="2"><span style="font-family: Arial, sans-serif; color: black;">Sáng</span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n  <td width="631" style="width:473.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  text-align:justify;line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">07h00</span></b><span style="font-family: Arial, sans-serif; color: black;">: Quý khách ăn sáng\r\n  buffet tại khách sạn. Sau đó xe và hướng dẫn viên đón khách khởi hành đi Huế,\r\n  đoàn chiêm ngưỡng vẻ đẹp hùng vĩ của&nbsp;<a href="http://danangsensetravel.com/deo-hai-van-n.html"><b><span style="color: rgb(23, 66, 121);">đèo Hải Vân</span></b></a>&nbsp;được\r\n  mệnh danh là "<b><i>Thiên hạ đệ nhất hùng quan</i></b>".</span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n </tr>\r\n <tr>\r\n  <td width="103" style="width:77.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  line-height:normal"><font size="2"><span style="font-family: Arial, sans-serif; color: black;">Trưa</span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n  <td width="631" style="width:473.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  text-align:justify;line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">11h30:</span></b><span style="font-family: Arial, sans-serif; color: black;">&nbsp; Ăn trưa nhà\r\n  hàng, nhận phòng khách sạn nghỉ ngơi.</span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n </tr>\r\n <tr>\r\n  <td width="103" style="width:77.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  line-height:normal"><font size="2"><span style="font-family: Arial, sans-serif; color: black;">Chiều</span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n  <td width="631" style="width:473.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  text-align:justify;line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">13h30</span></b><span style="font-family: Arial, sans-serif; color: black;">: Tham quan<b>&nbsp;Đại\r\n  Nội</b>&nbsp;(Hoàng Cung của 13 vị vua triều Nguyễn, triều đại phong kiến\r\n  cuối cùng của Việt Nam: Ngọ Môn, Điện Thái Hoà, Tử Cấm Thành, Thế Miếu, Hiển\r\n  Lâm Các, Cửu Đỉnh) và&nbsp;<b>Chùa Thiên Mụ&nbsp;</b>cổ kính, xây dựng từ\r\n  những năm đầu của thế kỉ XVII.</span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n </tr>\r\n <tr>\r\n  <td width="103" style="width:77.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  line-height:normal"><font size="2"><span style="font-family: Arial, sans-serif; color: black;">Tối</span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n  <td width="631" style="width:473.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  text-align:justify;line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">18h30:</span></b><span style="font-family: Arial, sans-serif; color: black;">&nbsp;Ăn tối nhà hàng\r\n  với đặc sản xứ Huế (Bánh bèo, lọc, nậm, khoái,...).&nbsp; Quý khách tự do\r\n  khám phá Huế về đêm, quý khách có thể dạo chơi trên<b>&nbsp;Bến Tòa Khâm</b>&nbsp;xuống\r\n  thuyền Rồng để thưởng thức&nbsp;<b>ca Huế trên sông Hương</b>&nbsp;- nét văn\r\n  hóa độc đáo của xứ Huế (chi phí tự túc 100.000vnd/ 1 khách).</span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n </tr>\r\n <tr>\r\n  <td width="103" style="width:77.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">NGÀY 03</span></b><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n  <td width="631" style="width:473.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  text-align:justify;line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">ĐỘNG THIÊN ĐƯỜNG –\r\n  HUẾ&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n  &nbsp;(Ăn: sáng, trưa, tối)</span></b><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n </tr>\r\n <tr>\r\n  <td width="103" style="width:77.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">Sáng</span></b><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n  <td width="631" style="width:473.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  text-align:justify;line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">07h00:</span></b><span style="font-family: Arial, sans-serif; color: black;">&nbsp;Qúy khách dùng\r\n  điểm tâm sáng tại khách sạn. Sau đó xe và hướng dẫn viên đón quý khách khởi\r\n  hành đi<b>&nbsp;Quảng Bình</b>. Trên đường đi, quý khách thăm quan&nbsp;<b>Thánh\r\n  địa La Vang</b>(nơi Đức Mẹ đã hiện ra tại vùng đất này vào năm 1798, và ngang\r\n  qua&nbsp;<b>Vĩ tuyến 17 Sông Bến Hải</b>&nbsp;–&nbsp;<b>cầu Hiền Lương</b>&nbsp;huyền\r\n  thoại.</span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n </tr>\r\n <tr>\r\n  <td width="103" style="width:77.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">Trưa</span></b><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n  <td width="631" style="width:473.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  text-align:justify;line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">11h30:&nbsp;</span></b><span style="font-family: Arial, sans-serif; color: black;">Dùng\r\n  cơm trưa tại nhà hàng&nbsp;<b>Động Thiên Đường.</b></span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n </tr>\r\n <tr>\r\n  <td width="103" style="width:77.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">Chiều</span></b><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n  <td width="631" style="width:473.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  text-align:justify;line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">13h30:&nbsp;</span></b><span style="font-family: Arial, sans-serif; color: black;">Quý\r\n  khách thăm quan&nbsp;<b>Động Thiên Đường</b>, khám phá vẻ đẹp đẹp huyền bí\r\n  của động khô dài nhất Châu Á, là kỳ quan thiên nhiên của thế giới với những\r\n  tuyệt mỹ của tạo hóa ban tặng. Quý khách sẽ có cơ hội trải nghiệm từng cung\r\n  bậc của chốn bồng lai tiên cảnh, thiên đình.</span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  text-align:justify;line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">17h00:</span></b><span style="font-family: Arial, sans-serif; color: black;">&nbsp;Xe đón quý khách\r\n  khởi hành về Huế theo tuyến đường Trường Sơn – Hồ Chí Minh huyền thoại.</span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n </tr>\r\n <tr>\r\n  <td width="103" style="width:77.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">Tối</span></b><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n  <td width="631" style="width:473.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  text-align:justify;line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">19h30:</span></b><span style="font-family: Arial, sans-serif; color: black;">&nbsp;Qúy khách dùng\r\n  bữa tối tại nhà hàng, sau bữa tối quý khách tự do khám phá Huế về đêm với cầu\r\n  Trường Tiền rực rỡ soi mình xuống dòng sông Hương thơ mộng dạo chơi chợ đêm\r\n  Huế để thưởng thức nét sinh hoạt về đêm của đất Cố Đô.</span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n </tr>\r\n <tr>\r\n  <td width="103" style="width:77.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">NGÀY 04</span></b><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n  <td width="631" style="width:473.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  text-align:justify;line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">LĂNG KHẢI ĐỊNH - HÀ NỘI/ TP HỒ CHÍ MINH &nbsp; &nbsp; &nbsp;\r\n  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n  &nbsp; (Ăn sáng, trưa)</span></b><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n </tr>\r\n <tr>\r\n  <td width="103" style="width:77.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">Sáng</span></b><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n  <td width="631" style="width:473.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  text-align:justify;line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">07h00:</span></b><span style="font-family: Arial, sans-serif; color: black;">&nbsp;Quý khách dùng\r\n  điểm tâm sáng tại khách sạn. Sau đó xe đưa quý khách thăm quan Tham quan<b>&nbsp;Lăng\r\n  Khải Định</b>&nbsp;- với phòng cách kiến trúc tuyệt vời kết hợp giữa hai\r\n  trường phái văn hóa&nbsp;<b>Đông - Tây</b>&nbsp;và tiếp tục tham quan&nbsp;<b>Chùa\r\n  Từ Hiếu -&nbsp;</b>ngôi chùa độc nhất vô nhị với sự tích báo hiếu mẹ gây xúc\r\n  động lòng người.</span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n </tr>\r\n <tr>\r\n  <td width="103" style="width:77.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">Trưa</span></b><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n  <td width="631" style="width:473.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  text-align:justify;line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">11h00:&nbsp;</span></b><span style="font-family: Arial, sans-serif; color: black;">Qúy\r\n  khách trả phòng, thưởng thức đặc sản Huế tại nhà hàng, thưởng thức không gian\r\n  café và mua sắm đặc sản, quà lưu niệm tại Đà Nẵng Souvenir.</span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n </tr>\r\n <tr>\r\n  <td width="103" style="width:77.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">Chiều</span></b><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n  <td width="631" style="width:473.25pt;padding:0in 0in 0in 0in">\r\n  <p class="MsoNormal" style="mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\n  text-align:justify;line-height:normal"><font size="2"><b><span style="font-family: Arial, sans-serif; color: black;">13h30:</span></b><span style="font-family: Arial, sans-serif; color: black;">&nbsp;Xe đưa quý khách\r\n  ra Ga Huế/ Sân bay Phú Bài / Sân bay Đà Nẵng. Chia tay quý khách, hẹn gặp lại\r\n  quý khách trong những hành trình kế tiếp.</span><span style="font-family: Arial, sans-serif; color: black;"><o:p></o:p></span></font></p>\r\n  </td>\r\n </tr>\r\n</tbody></table>', '<p class="MsoNormal" style="line-height: normal; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;"><font size="2"><b><u><span